import { GitHubFileStatus } from "../githubTypes";
export declare type Trigger = {
    id: string;
    billingAccountId: string;
    /**
     * The trigger's name
     */
    name: string;
    repoOrigin: string;
    /**
     * A Trigger will only execute if it is enabled
     */
    enabled: boolean;
    /**
     * Triggers will only execute on draft PRs if `applyToDrafts` and `enabled`
     * are both true.
     */
    applyToDrafts: boolean;
    conditions: TriggerCondition[];
    actions: TriggerAction[];
    authorIsCurrentUser: boolean;
    createdAt: string;
};
export declare type TriggerWithRepoId = Omit<Trigger, "repoOrigin"> & {
    repoId: string;
};
export declare type CreateTriggerPayload = Omit<Trigger, "id" | "authorIsCurrentUser" | "createdAt">;
export declare type UpdateTriggerPayload = Partial<Omit<Trigger, "id" | "billingAccountId" | "authorIsCurrentUser" | "repoOrigin" | "createdAt">>;
export declare type TriggerCondition = {
    field: TriggerConditionField;
    operator: TriggerConditionOperator;
    value: string;
};
export declare enum TriggerConditionField {
    FileContents = "file contents",
    FileName = "file name",
    FileAddedInFolder = "file added in folder",
    FilePath = "file path"
}
export declare enum TriggerConditionOperator {
    Equals = "equals",
    Contains = "contains",
    MatchesGlob = "matches glob",
    StartsWith = "starts with",
    EndsWith = "ends with"
}
export declare type TriggerAction = TriggerActionAddToChecklist | TriggerActionAddReviewer | TriggerActionAddAssignee | TriggerActionAddComment;
export declare type TriggerActionAddToChecklist = {
    kind: TriggerActionKind.AddToChecklist;
    content: string;
    id: string;
};
export declare type TriggerActionReviewerType = "user" | "team" | "unknown";
export declare type TriggerActionAddReviewer = {
    kind: TriggerActionKind.AddReviewer;
    /**
     * For users, this is their GitHub username, ie "jleven". For teams, this is
     * the slug, ie "engineers".
     */
    githubLogin: string;
    /**
     * Keep track of whether the `githubLogin` field refers to a user or a team.
     *
     * Note that the TriggerActionAddAssignee can only apply to GitHub users, so
     * it doesn't contain this field.
     */
    reviewerType: TriggerActionReviewerType;
    message?: string;
    id: string;
};
export declare type TriggerActionAddAssignee = {
    kind: TriggerActionKind.AddAssignee;
    githubLogin: string;
    message?: string;
    id: string;
};
export declare type TriggerActionAddComment = {
    kind: TriggerActionKind.AddComment;
    content: string;
    id: string;
};
export declare enum TriggerActionKind {
    AddToChecklist = "add to checklist",
    AddReviewer = "add reviewer",
    AddAssignee = "add assignee",
    AddComment = "add comment"
}
export declare const FileChangeType: typeof GitHubFileStatus;
export declare type FileChange = {
    filepath: string;
    changeType: GitHubFileStatus;
    patch: string[];
};
