/**
 * Returns the repo's owner (organization), name, and owner URL from a full GitHub origin.
 * @example getGitHubRepoDetailsFromOrigin("https://github.com/Codesee-io/oss-port")
 * {
 *   repoOwner: "Codesee-io",
 *   repoName: "oss-port",
 *   repoOwnerUrl: "https://github.com/Codesee-io"
 * }
 */
export declare function getGitHubRepoDetailsFromOrigin(repoOrigin: string): {
    repoOwner: string;
    repoName: string;
    repoOwnerUrl: string;
};
/**
 * Returns `true` if the repo origin is from GitHub.
 * @example repoOriginIsGitHub("https://github.com/Codesee-io") // true
 * @example repoOriginIsGitHub("https://github.com/Codesee-io/oss-port") // true
 * @example repoOriginIsGitHub("https://api.github.com") // false
 * @example repoOriginIsGitHub("https://gitlab.com/Codesee-io") // false
 */
export declare function repoOriginIsGitHub(repoOrigin: string): boolean;
/**
 * Formats a repo origin into an owner/name string.
 *
 * @example getGitHubRepoShortNameFromOrigin("https://github.com/Codesee-io/codesee")
 * // "Codesee-io/codesee"
 */
export declare function getGitHubRepoShortNameFromOrigin(repoOrigin?: string): string;
